import React from 'react';
import { useMediaQuery } from 'react-responsive';
import TodoDesktopInner from './desktop/todoDesktopInner.component';
import NewButton from './actions/newButton.component';
import TodoTabletInner from './tablet/todoTabletInner.component';
import TodoMobileInner from './mobile/todoMobileInner.component';

export default function TodoContainer() {
  const isMobile = useMediaQuery({ maxWidth: 425 });
  const isTablet = useMediaQuery({ minWidth: 425, maxWidth: 1024 });
  const isDesktop = useMediaQuery({ minWidth: 1024 });

  // TODO: return element for each case from high to low width

  // if (isDesktop) {
  //   return (
  //     <>
  //       <TodoDesktopInner />
  //       <NewButton />
  //     </>
  //   );
  // }

  // if (isTablet) {
  //   <TodoTabletElement />;
  // }

  return (
    <>
      {isMobile && (
        <>
          <h1>This is mobile</h1>
          <TodoMobileInner />
        </>
      )}
      {isTablet && (
        <>
          <h1>This is tablet</h1>
          <TodoTabletInner />
        </>
      )}
      {isDesktop && (
        <>
          <TodoDesktopInner />
          <NewButton />
        </>
      )}
    </>
  );
}
