import React from 'react';
import { useDeviceType } from '../../../../utils/useDeviceType';
import TodoDesktopInner from './desktop/todoDesktopInner.component';
import NewButton from './actions/new-button.component';
import TodoTabletInner from './tablet/todo-tablet-inner.component';
import TodoMobileInner from '../../../todo/components/mobile/todo-mobile-inner.component';

export const TodoMainContainer = () => {
  const { isMobile, isTablet, isDesktop } = useDeviceType();

  return (
    <>
      {isMobile && (
        <>
          <TodoMobileInner />
          <NewButton />
        </>
      )}
      {isTablet && (
        <>
          <TodoTabletInner />
          <NewButton />
        </>
      )}
      {isDesktop && (
        <>
          <TodoDesktopInner />
          <NewButton />
        </>
      )}
    </>
  );
};

export default TodoMainContainer;
