import React from 'react';
import styled from 'styled-components';
import { Swiper, SwiperSlide } from 'swiper/react';
import { EffectCoverflow } from 'swiper';
import 'swiper/css';
import { useQueryClient } from 'react-query';
import { QUERY_KEYS } from '../../../consts/app-keys.const';
import { ITodo } from '../../../types/todo.types';
import TodoTabletElement from './todoTabletElement.component';

const StyledSwiperContainer = styled.div`
  margin-top: 2rem;
  .swiper-slide-active {
    // transform: scale(1.8);
    // background: grey;
  }

  .slide {
    border: 2px solid grey;
  }

  .swiper {
    width: 50%;
    padding-top: 50px;
    padding-bottom: 50px;
  }

  .swiper-slide {
    background-position: center;
    background-size: cover;
    width: 300px;
    height: 300px;
    padding: 2rem;
  }

  .slide-actions {
    display: flex;
    align-items: center;
    justify-content: space-around;
  }

  .slide-actions-buttons {
    width: 50%;
  }

  .slide-actions-buttons div:first-child {
    margin-right: 1rem;
  }
`;

const TodoTabletInner = () => {
  const queryClient = useQueryClient();
  const todos = queryClient.getQueryData<ITodo[]>(QUERY_KEYS.TODOS);

  return (
    <StyledSwiperContainer>
      <Swiper
        effect={'coverflow'}
        grabCursor
        centeredSlides
        slidesPerView={1}
        loop
        coverflowEffect={{
          // rotate: 50,
          // stretch: 0,
          depth: 50,
          modifier: 1,
          slideShadows: true
        }}
        pagination
        modules={[EffectCoverflow]}
        className="mySwiper"
      >
        {todos?.map((todo) => (
          <SwiperSlide key={todo.id} className="slide">
            <TodoTabletElement todo={todo} />
          </SwiperSlide>
        ))}
      </Swiper>
    </StyledSwiperContainer>
  );
};

export default TodoTabletInner;
