import { HttpService, HttpConfig } from './HttpService';

export class TodoService extends HttpService {
  async getAllTodos() {
    const config: HttpConfig = {
      url: '/todos'
    };

    const res = await this.get(config);

    return res.data;
  }

  createTodo(data: any) {
    const config: HttpConfig = {
      url: '/todos',
      method: 'POST',
      data
    };

    return this.get(config);
  }

  updateTodo(todoId: string, data: any) {
    const config: HttpConfig = {
      url: `/todos/${todoId}`,
      method: 'PUT',
      data
    };

    return this.put(config);
  }

  deleteTodo(todoId: string) {
    const config: HttpConfig = {
      url: `/todos/${todoId}`,
      method: 'DELETE'
    };

    return this.delete(config);
  }
}
