import React from 'react';
import { Formik, Field, Form } from 'formik';
import styled from 'styled-components';
import { DefaultPageContainer } from '../default/defaultPageContainer.styled';
import { TodoService } from '../../services/TodoService';

const FormContainer = styled.div`
  form {
    display: flex;
    flex-direction: column;
    width: 300px;
    margin: 0 auto;
  }

  label {
    font-weight: bold;
    margin-bottom: 5px;
  }

  input[type='text'],
  input[type='checkbox'] {
    padding: 8px;
    margin-bottom: 10px;
    border: 1px solid #ccc;
    border-radius: 4px;
  }

  button {
    padding: 10px 15px;
    background-color: #007bff;
    color: #fff;
    border: none;
    border-radius: 4px;
    cursor: pointer;
  }

  button:hover {
    background-color: #0056b3;
  }
`;

const todoService = new TodoService();

const CreateTodoPageContainer = () => {
  const test = 0;
  return (
    <DefaultPageContainer>
      <h1>this is a create page</h1>
      <Formik
        initialValues={{
          title: '',
          description: '',
          isCompleted: false,
          isPrivate: false
        }}
        onSubmit={async (values) => {
          await todoService.createTodo(values);
        }}
      >
        <Form>
          <label htmlFor="title">Title</label>
          <Field id="title" name="title" placeholder="Type title" />

          <label htmlFor="description">Description</label>
          <Field id="description" name="description" />

          <label htmlFor="isCompleted">Completed</label>
          <Field id="isCompleted" name="isCompleted" type="checkbox" />

          <label htmlFor="isPrivate">Private</label>
          <Field id="isPrivate" name="isPrivate" type="checkbox" />

          <button type="submit">Create</button>
        </Form>
      </Formik>
    </DefaultPageContainer>
  );
};

export default CreateTodoPageContainer;
