import styled from 'styled-components';

export const StyledMobileContainer = styled.div`
  .todo-item-mobile {
    position: relative;
    margin-bottom: 10px;
  }

  .actions {
    // margin-bottom: 1rem;
  }

  // .todo-item-mobile:not(:last-child) {
  //   margin-bottom: 1rem;
  // }

  .todo-item-mobile:not(:last-child)::after {
    content: '';
    position: absolute;
    bottom: -1rem;
    left: 0;
    width: 100%;
    height: 1px;
    background-color: #ccc; /* Choose your desired line color */
  }
`;
