import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { useQueryClient } from 'react-query';
import { ToggleButton } from './actions/toggle.styled';
import { TodoService } from '../../../../services/TodoService';
import { invalidateQuery } from '../../../../utils/invalidateQuery';
import { QUERY_KEYS } from '../../consts/app-keys.const';

const todoService = new TodoService();

export default function TodoPageContainer() {
  const location = useLocation();
  const { initialTodo } = location.state;
  console.log(location.state);
  const [todo, setTodo] = useState<any>(initialTodo);
  // const queryClient = useQueryClient();

  const updateTodo = async (updatedTodo: any) => {
    await todoService.updateTodo(todo.id, updatedTodo);
  };

  // const fetchTodo = async () => {
  //   const { data } = await todoService.getById(todoId);

  //   return data;
  // };

  useEffect(() => {
    const update = async () => {
      await updateTodo(todo);
      invalidateQuery(QUERY_KEYS.TODOS);
    };

    update();
  }, [todo]);

  const privateToggle = async () => {
    console.log('private toggle');
    const updatedTodo = { ...todo };
    updatedTodo.isPrivate = !updatedTodo.isPrivate;
    setTodo(updatedTodo);
  };
  const completeToggle = () => console.log('complete toggle');

  return (
    <>
      <h1>{todo.title}</h1>
      <div>{todo.id}</div>
      <div>{todo.description}</div>
      <div>
        Complete: <ToggleButton isOn={todo.isCompleted} onToggle={completeToggle} />
      </div>
      <div>
        Private: <ToggleButton isOn={todo.isPrivate} onToggle={privateToggle} />
      </div>
    </>
  );
}
