    background-color: #333;
    color: white;
  }

  tbody tr:nth-child(even) {
    background-color: #f2f2f2;
  }

  tbody tr:hover {
    background-color: #d4d4d4;
  }

  /* Example Borders */
  table {
    border: 1px solid #ddd;
  }

  th, td {
    border-bottom: 1px solid #ddd;
  }

  th:last-child, td:last-child {
    border-right: 1px solid #ddd;
  }
`;
