import React, { PropsWithChildren } from 'react';
import { useMutation, useQueryClient } from 'react-query';
import { useHistory } from 'react-router-dom';
import { TodoService } from '../../../../../services/TodoService';
import { QUERY_KEYS } from '../../../consts/app-keys.const';
import { StyledDeleteButton } from '../actions/deleteTodo.styled';
import { StyledViewButton } from '../actions/viewTodo.styled';

const todoService = new TodoService();

interface Todo {
  id: string;
  title: string;
  description: string;
  isPrivate: boolean;
}

type Props = PropsWithChildren<{
  todo: Todo;
}>;

export default function TodoElement({ todo }: Props) {
  const queryClient = useQueryClient();

  async function deleteTodo(id: string) {
    await todoService.deleteTodo(id);
  }

  const mutation = useMutation(deleteTodo, {
    onSuccess: () => {
      queryClient.invalidateQueries(QUERY_KEYS.TODOS);
    }
  });

  function deleteHandler(id: string) {
    mutation.mutate(id);
  }

  function handleViewClick() {
    const history = useHistory();
    history.push('/todo', { todo });
  }

  return (
    <tr>
      <td>{todo.title}</td>
      <td>{todo.description}</td>
      <td>
        <StyledViewButton onClick={() => handleViewClick}>View</StyledViewButton>
        <StyledDeleteButton onClick={(e) => deleteHandler(e.currentTarget.id)} id={todo.id}>
          Delete
        </StyledDeleteButton>
      </td>
    </tr>
  );
}
