import React from 'react';
import { useQuery } from 'react-query';
import { useMediaQuery } from 'react-responsive';
import TodoContainer from '../common/components/todo/TodoContainer';
import { TodoService } from '../../services/TodoService';
import { QUERY_KEYS } from '../common/consts/app-keys.const';

const todoService = new TodoService();

async function fetchTodos() {
  const { data } = await todoService.getAllTodos();
  return data;
}

const HomePageContainer = () => {
  const { isLoading, error } = useQuery(QUERY_KEYS.TODOS, fetchTodos);
  const isMobile = useMediaQuery({ minWidth: 320, maxWidth: 425 });
  const isTablet = useMediaQuery({ minWidth: 425, maxWidth: 768 });
  const isDesktop = useMediaQuery({ minWidth: 768, maxWidth: 2560 });

  if (isLoading) {
    return <h1>Is loading...</h1>;
  }

  if (error) {
    return <h3>Error, can not fetch todos</h3>;
  }

  return (
    <>
      <div>React is ready HOME!</div>
      {isMobile && <h1>This is mobile</h1>}
      {isTablet && <h1>This is tablet</h1>}
      {isDesktop && <TodoContainer />}
    </>
  );
};

export default HomePageContainer;
