import React, { PropsWithChildren } from 'react';
import { useMutation, useQueryClient } from 'react-query';
import styled from 'styled-components';
import DeleteTodo from './actions/deleteTodo.component';
import { TodoService } from '../../../../services/TodoService';
import { QUERY_KEYS } from '../../consts/app-keys.const';

const todoService = new TodoService();

const StyledDeleteButton = styled.button`
  background-color: palevioletred;
  color: white;
`;

interface Todo {
  id: string;
  title: string;
  description: string;
  isPrivate: boolean;
}

type Props = PropsWithChildren<{
  todo: Todo;
}>;


export default function TodoElement({ todo }: Props) {
  const queryClient = useQueryClient();
  const mutation = useMutation(deleteHandler, {
    onSuccess: () => {
      queryClient.invalidateQueries(QUERY_KEYS.TODOS);
    }
  });

  async function deleteTodo(id: string) {
    const res = await todoService.deleteTodo(id);
    console.log(res);
  }

  async function deleteHandler(e: React.MouseEvent) {
    await deleteTodo(e.currentTarget.id);
  }

  return (
    <tr>
      <td>{todo.title}</td>
      <td>{todo.description}</td>
      <td>
        <button type="button">Action</button>
        <StyledDeleteButton onClick={deleteHandler} id={todo.id}>
          Delete
        </StyledDeleteButton>
      </td>
    </tr>
  );
}
