import React from 'react';
import { useLocation } from 'react-router-dom';
import { useQuery } from 'react-query';
import { ToggleButton } from './actions/toggle.styled';
import { TodoService } from '../../../../services/TodoService';
import { QUERY_KEYS } from '../../consts/app-keys.const';

const todoService = new TodoService();

export default function TodoPageContainer() {
  const location = useLocation();
  const { todoId } = location.state;

  const updateTodo = async (updatedTodo: any) => {
    await todoService.updateTodo(todoId, updatedTodo);
  };

  const fetchTodo = async () => {
    const { data } = await todoService.getById(todoId);

    return data;
  };

  const { data, isLoading } = useQuery(QUERY_KEYS.TODOID, fetchTodo);
  const todo = data?.data.todo;

  const privateToggle = async () => {
    const updatedTodo = { ...todo };
    updatedTodo.isPrivate = !updatedTodo.isPrivate;
    await updateTodo(updatedTodo);
  };
  const completeToggle = async () => {
    const updatedTodo = { ...todo };
    updatedTodo.isCompleted = !updatedTodo.isCompleted;
    await updateTodo(updatedTodo);
  };

  return (
    <div>
      {isLoading ? (
        <p>Loading..</p>
      ) : (
        <div>
          <h1>{todo.title}</h1>
          <div>{todo.id}</div>
          <div>{todo.description}</div>
          <div>
            Complete: <ToggleButton isOn={todo.isCompleted} onToggle={completeToggle} />
          </div>
          <div>
            Private: <ToggleButton isOn={todo.isPrivate} onToggle={privateToggle} />
          </div>
        </div>
      )}
    </div>
  );
}
