import React from 'react';
import styled from 'styled-components';
import { Swiper, SwiperSlide } from 'swiper/react';
import { EffectCoverflow } from 'swiper';
import 'swiper/css';
import { useQueryClient } from 'react-query';
import { useNavigate } from 'react-router-dom';
import { QUERY_KEYS, ROUTER_KEYS } from '../../../consts/app-keys.const';
import { ITodo } from '../../../types/todo.types';
import { StyledViewButton } from '../actions/viewTodo.styled';

const StyledItem = styled.div`
  // min-width: 100px;
  // min-height: 50px;
  // border: 1px solid black;
  // text-align: center;
`;

const StyledContainer = styled.div`
  margin-top: 2rem;
  .swiper-slide-active {
    // transform: scale(1.8);
    // background: grey;
  }

  .slide {
    border: 2px solid grey;
  }

  .swiper {
    width: 50%;
    padding-top: 50px;
    padding-bottom: 50px;
  }

  .swiper-slide {
    background-position: center;
    background-size: cover;
    width: 300px;
    height: 300px;
    padding: 2rem;
  }

  // body {
  //   position: relative;
  // }
`;

const TodoTabletInner = () => {
  const queryClient = useQueryClient();
  const todos = queryClient.getQueryData<ITodo[]>(QUERY_KEYS.TODOS);

  const navigate = useNavigate();

  const handleViewClick = () => {
    navigate(ROUTER_KEYS.TODO, { state: { todoId: todo.id } });
  };

  return (
    <StyledContainer>
      <Swiper
        effect={'coverflow'}
        grabCursor
        centeredSlides
        slidesPerView={1}
        loop
        coverflowEffect={{
          // rotate: 50,
          // stretch: 0,
          depth: 50,
          modifier: 1,
          slideShadows: true
        }}
        pagination
        modules={[EffectCoverflow]}
        className="mySwiper"
      >
        {todos?.map((todo) => (
          <SwiperSlide className="slide">
            <h2 className="slide-title">{todo.title}</h2>
            <div className="slide-description">{todo.description}</div>
            <div className="slide-actions">
              <StyledViewButton onClick={handleViewClick}>View</StyledViewButton>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </StyledContainer>
  );
};

export default TodoTabletInner;
