import React, { useState } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import styled from 'styled-components';
import { DefaultPageContainer } from '../default/defaultPageContainer.styled';
import { defaultTodoService } from './services/todo.service';
import BackButton from '../common/components/back-button.component';

const FormContainer = styled.div`
  form {
    display: flex;
    flex-direction: column;
    width: 300px;
    margin: 0 auto;
  }

  label {
    font-weight: bold;
    margin-bottom: 5px;
  }

  input[type='text'],
  input[type='checkbox'] {
    padding: 8px;
    margin-bottom: 10px;
    border: 1px solid #ccc;
    border-radius: 4px;
  }

  button {
    padding: 10px 15px;
    background-color: #007bff;
    color: #fff;
    border: none;
    border-radius: 4px;
    cursor: pointer;
  }

  button:hover {
    background-color: #0056b3;
  }
`;

const CreateTodoPageContainer = () => {
  const validationSchema = Yup.object().shape({
    title: Yup.string().required('Title is required'),
    description: Yup.string().required('Description is required')
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submissionError, setSubmissionError] = useState<string | null>(null);
  const [submissionSuccess, setSubmissionSuccess] = useState(false);

  return (
    <DefaultPageContainer>
      <h1>this is a create page</h1>
      <Formik
        initialValues={{
          title: '',
          description: '',
          isCompleted: false,
          isPrivate: false
        }}
        validationSchema={validationSchema}
        onSubmit={async (values, { resetForm, setStatus, setSubmitting }) => {
          setSubmitting(true);
          try {
            await defaultTodoService.createTodo(values);
            resetForm();
            setSubmissionSuccess(true);
            setStatus(null);
          } catch (error) {
            if (error instanceof Error) {
              setSubmissionError(error.message);
            }
          } finally {
            setIsSubmitting(false);
          }
        }}
      >
        <FormContainer>
          <Form>
            <label htmlFor="title">Title</label>
            <Field id="title" name="title" placeholder="Type title" />
            <ErrorMessage name="title" component="div" />

            <label htmlFor="description">Description</label>
            <Field id="description" name="description" />
            <ErrorMessage name="description" component="div" />

            <label htmlFor="isCompleted">Completed</label>
            <Field id="isCompleted" name="isCompleted" type="checkbox" />

            <label htmlFor="isPrivate">Private</label>
            <Field id="isPrivate" name="isPrivate" type="checkbox" />

            {submissionSuccess && <div>Todo created successfully</div>}
            {submissionError && (
              <div>
                Error ocurred while creating todo! <br /> {submissionError}
              </div>
            )}

            <button type="submit">{isSubmitting ? 'Creating...' : 'Create'}</button>
          </Form>
        </FormContainer>
      </Formik>
      <BackButton />
    </DefaultPageContainer>
  );
};

export default CreateTodoPageContainer;
