import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { useQuery } from 'react-query';
import styled from 'styled-components';
import { ToggleButton } from './actions/toggle.component';
import { TodoService } from '../../../../services/TodoService';
import { QUERY_KEYS } from '../../consts/app-keys.const';
import { ITodo } from '../../types/todo.types';
import { CompleteToggle } from './actions/completeToggle.component';

const todoService = new TodoService();

const StyledContainer = styled.div`
  max-width: 90%;
  margin: 5% auto;

  .title {
    margin-bottom: 2%;
  }

  .description {
    margin-top: 1%;
    margin-left: 2%;
    font-size: 16px;
    font-weight: normal;
    color: #555555;
    line-height: 1.5;
    padding: 8px 0;
  }
`;

const TodoPageContainer = () => {
  const location = useLocation();
  const { todoId } = location.state;

  const updateTodo = async (updatedTodo: ITodo) => {
    await todoService.updateTodo(todoId, updatedTodo);
  };

  const fetchTodo = async () => {
    const { data } = await todoService.getById(todoId);

    return data;
  };

  const { data, isLoading, refetch } = useQuery(QUERY_KEYS.TODOID, fetchTodo, {
    enabled: true
  });
  const [todo, setTodo] = useState<ITodo>(data.data.todo);
  // const { todo }: { todo: ITodo } = data && data.data ? data.data : {};

  if (todo && todo.id !== todoId) {
    console.log('refetch');
    console.log(todo);
    refetch();
    setTodo(data.data.todo);
  }
  // refetch();

  const privateToggle = async () => {
    const updatedTodo = { ...todo };
    updatedTodo.isPrivate = !updatedTodo.isPrivate;
    await updateTodo(updatedTodo);
  };

  // const completeToggle = async () => {
  //   const updatedTodo = { ...todo };
  //   updatedTodo.isCompleted = !updatedTodo.isCompleted;
  //   await updateTodo(updatedTodo);
  // };

  return (
    <StyledContainer>
      {isLoading ? (
        <p>Loading..</p>
      ) : (
        <div className="inner">
          <div>{data.data.todo.title}</div>
          <h1 className="title">{todo.title}</h1>
          <div>Description:</div>
          <div className="description">{todo.description}</div>
          <div>
            {/* Complete: <ToggleButton isOn={todo.isCompleted} onToggle={completeToggle} /> */}
            Complete: <CompleteToggle todo={todo} />
          </div>
          <div>
            Private: <ToggleButton isOn={todo.isPrivate} onToggle={privateToggle} />
          </div>
        </div>
      )}
    </StyledContainer>
  );
};

export default TodoPageContainer;
