import React from 'react';
import { useMediaQuery } from 'react-responsive';
import TodoDesktopInner from './desktop/todoDesktopInner.component';
import NewButton from './actions/newButton.component';
import TodoTabletElement from './tablet/todoTabletElement.component';

export default function TodoContainer() {
  const isMobile = useMediaQuery({ minWidth: 320 });
  const isTablet = useMediaQuery({ minWidth: 768, maxWidth: 1444 });
  const isDesktop = useMediaQuery({ minWidth: 1444, maxWidth: 2500 });

  // TODO: return element for each case from high to low width

  // if (isDesktop) {
  //   return (
  //     <>
  //       <TodoDesktopInner />
  //       <NewButton />
  //     </>
  //   );
  // }

  // if (isTablet) {
  //   <TodoTabletElement />;
  // }

  return (
    <>
      {isMobile && <h1>This is mobile</h1>}
      {isTablet && <h1>This is tablet</h1>}
      {isDesktop && (
        <>
          <TodoDesktopInner />
          <NewButton />
        </>
      )}
    </>
  );
}
