import React from 'react';
// import TodoElement from './TodoElement';
import { useMediaQuery } from 'react-responsive';
import TodoDesktopInner from './desktop/todoDesktopInner.component';

// type Props = PropsWithChildren<{
//   todos: any;
// }>;

export default function TodoContainer() {
  const isMobile = useMediaQuery({ minWidth: 320, maxWidth: 425 });
  const isTablet = useMediaQuery({ minWidth: 425, maxWidth: 768 });
  const isDesktop = useMediaQuery({ minWidth: 768, maxWidth: 2560 });

  if (isMobile) {
    return <h1>This is mobile</h1>;
  }

  if (isTablet) {
    return <h1>This is tablet</h1>;
  }

  if (isDesktop) {
    return <TodoDesktopInner />;
  }

  return (
    <>
      {isMobile && <h1>This is mobile</h1>}
      {isTablet && <h1>This is tablet</h1>}
      {isDesktop && <TodoDesktopInner />}
    </>

  );
}
