import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { useQuery } from 'react-query';
import styled from 'styled-components';
import { TodoService } from '../../services/TodoService';
import { QUERY_KEYS } from '../common/consts/app-keys.const';
import { ITodo } from '../common/types/todo.types';
import { CompleteToggle } from '../common/components/todo/actions/completeToggle.component';
import { PrivateToggle } from '../common/components/todo/actions/privateToggle.component';
import BackButton from '../common/components/actions/backButton.component';

const todoService = new TodoService();

const StyledContainer = styled.div`
  max-width: 90%;
  margin: 5% auto;

  .title {
    margin-bottom: 2%;
  }

  .description {
    margin-top: 1%;
    margin-left: 2%;
    font-size: 16px;
    font-weight: normal;
    color: #555555;
    line-height: 1.5;
    padding: 8px 0;
  }

  .state {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 50%;
    padding: 1rem;
  }
`;

const TodoPageContainer = () => {
  const location = useLocation();
  const { todoId } = location.state;
  const [isEdit, setIsEdit] = useState(false);

  const fetchTodo = async () => {
    const { data } = await todoService.getById(todoId);

    return data.data.todo;
  };

  const { data, isLoading, refetch } = useQuery(QUERY_KEYS.TODOID, fetchTodo, {
    enabled: true
  });
  const [todo, setTodo] = useState<ITodo | null>(null);

  useEffect(() => {
    setTodo(data);
  }, [data]);

  if (data && data.id !== todoId) {
    refetch();
  }

  if (!todo) {
    return <p>No data available</p>;
  }

  return (
    <StyledContainer>
      {isLoading && <p>Loading..</p>}
      {!isLoading && !isEdit ? (
        <div className="inner">
          <h1 className="title">{todo.title}</h1>
          <div>Description:</div>
          <div className="description">{todo.description}</div>
          <div className="state">
            <span>Complete:</span> <CompleteToggle todo={todo} />
          </div>
          <div className="state">
            Private: <PrivateToggle todo={todo} />
          </div>
        </div>
      ) : (
        <p>editcomponent</p>
      )}
      <BackButton />
    </StyledContainer>
  );
};

export default TodoPageContainer;
