import styled from 'styled-components';

export const StyledCreatePageContainer = styled.div`
  max-width: 90%;
  margin: 5% auto;

  .title {
    margin-bottom: 2%;
  }

  .description {
    margin-top: 1%;
    margin-left: 2%;
    font-size: 16px;
    font-weight: normal;
    color: #555555;
    line-height: 1.5;
    padding: 8px 0;
  }

  .state {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 50%;
    padding: 1rem;
  }

  .actions {
    display: flex;
  }

  .edit-button {
    margin-left: 2rem;
  }
`;
