import React, { useState } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import styled from 'styled-components';
import { createTodoValidation } from './forms/validation/todo-validation';
import { DefaultPageContainer } from '../default/defaultPageContainer.styled';
import { defaultTodoService } from './services/todo.service';
import BackButton from '../common/components/back-button.component';
import CreateTodoForm from './forms/create-todo-form.component';

const FormContainer = styled.div`
  form {
    display: flex;
    flex-direction: column;
    width: 300px;
    margin: 0 auto;
  }

  label {
    font-weight: bold;
    margin-bottom: 5px;
  }

  input[type='text'],
  input[type='checkbox'] {
    padding: 8px;
    margin-bottom: 10px;
    border: 1px solid #ccc;
    border-radius: 4px;
  }

  button {
    padding: 10px 15px;
    background-color: #007bff;
    color: #fff;
    border: none;
    border-radius: 4px;
    cursor: pointer;
  }

  button:hover {
    background-color: #0056b3;
  }
`;

const CreateTodoPageContainer = () => (
  <DefaultPageContainer>
    <CreateTodoForm />
    <BackButton />
  </DefaultPageContainer>
);

export default CreateTodoPageContainer;
