import { useQuery } from 'react-query';
import { defaultTodoService } from '../modules/todo/services/todo.service';
import { QUERY_KEYS } from '../modules/common/consts/app-keys.const';
// import { ITodo } from '../modules/todo/types/todo.types';

export const useTodos = (todoId?: string) => {
  const fetchAllTodos = async () => {
    const { data } = await defaultTodoService.getAllTodos();

    return data.data.todos;
  };

  if (todoId) {
    const fetchTodo = async () => {
      const { data } = await defaultTodoService.getById(todoId);

      return data.data.todo;
    };

    return useQuery(QUERY_KEYS.TODOID, fetchTodo);
  }

  return useQuery(QUERY_KEYS.TODOS, fetchAllTodos);
};
