import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { useQuery, useQueryClient, useMutation } from 'react-query';
// import { defaultTodoService } from './services/todo.service';
import { QUERY_KEYS } from '../common/consts/app-keys.const';
import { ITodo } from './types/todo.types';
import { CompleteToggle } from './components/complete-toggle.component';
import { PrivateToggle } from './components/private-toggle.component';
import BackButton from '../common/components/back-button.component';
import { StyledCreatePageContainer } from './containers/todo-create-page.container.styled';
import UpdateTodoForm from './forms/update-todo-form.component';
import EditButton from './components/edit-button-component';
import { useTodos } from '../../utils/useTodos';

const TodoPageContainer = () => {
  const location = useLocation();
  const { todoId } = location.state;
  const [isEdit, setIsEdit] = useState(false);

  // const fetchTodo = async () => {
  //   const { data } = await defaultTodoService.getById(todoId);

  //   return data.data.todo;
  // };

  // const { data, isLoading, refetch } = useQuery(QUERY_KEYS.TODOID, fetchTodo, {
  //   enabled: true
  // });
  const queryClient = useQueryClient();
  const { data, isLoading } = useTodos(todoId);
  // const [todo, setTodo] = useState<ITodo>(data);

  // useEffect(() => {
  //   setTodo(data);
  // }, [data]);

  useEffect(() => {
    // refetch();
    console.log('invalidate');
    queryClient.invalidateQueries([QUERY_KEYS.TODOID]);
  }, [todoId, isEdit]);

  if (!todo) {
    return <p>No data available</p>;
  }

  return (
    <StyledCreatePageContainer>
      {isLoading && <p>Loading..</p>}
      {!isLoading && !isEdit ? (
        <div className="inner">
          <h1 className="title">{data.title}</h1>
          <div>Description:</div>
          <div className="description">{data.description}</div>
          <div className="state">
            <span>Complete:</span> <CompleteToggle todo={data} />
          </div>
          <div className="state">
            Private: <PrivateToggle todo={data} />
          </div>
        </div>
      ) : (
        <UpdateTodoForm setter={setIsEdit} todo={data} />
      )}
      <div className="actions">
        <BackButton />
        <EditButton className="edit-button" onClick={() => setIsEdit(!isEdit)} />
      </div>
    </StyledCreatePageContainer>
  );
};

export default TodoPageContainer;
